import React, { useState, useCallback, useEffect, useRef } from 'react';
import { useWallet } from '@solana/wallet-adapter-react';

const EnhancedWalletButton = ({ children, className = '', variant = 'primary', showAddress = false, ...props }) => {
  const { 
    wallet, 
    connect, 
    disconnect, 
    connecting, 
    connected, 
    wallets,
    select,
    publicKey
  } = useWallet();

  const [showWalletMenu, setShowWalletMenu] = useState(false);
  const [isPressed, setIsPressed] = useState(false);
  const [debugInfo, setDebugInfo] = useState('');
  const touchTimeoutRef = useRef();
  const debugTimeoutRef = useRef();

  // Debug logging with visual feedback
  const debug = useCallback((message) => {
    const timestamp = new Date().toLocaleTimeString();
    const debugMsg = `[${timestamp}] ${message}`;
    console.log(debugMsg);
    setDebugInfo(debugMsg);

    // Clear debug info after 3 seconds
    if (debugTimeoutRef.current) {
      clearTimeout(debugTimeoutRef.current);
    }
    debugTimeoutRef.current = setTimeout(() => setDebugInfo(''), 3000);
  }, []);

  // Monitor wallet state changes
  useEffect(() => {
    debug(`State: connected=${connected}, connecting=${connecting}, wallets=${wallets.length}, wallet=${wallet?.adapter?.name}`);
  }, [connected, connecting, wallets.length, wallet?.adapter?.name, debug]);

  // Handle wallet connection/disconnection
  const handleConnect = useCallback(async () => {
    debug('üîó Connect button clicked');

    try {
      if (connected) {
        debug('üîì Disconnecting current wallet...');
        await disconnect();
        debug('‚úÖ Wallet disconnected');
        return;
      }

      if (wallets.length === 0) {
        debug('‚ùå No wallets detected');
        const message = 'No Solana wallets found.\n\nPlease install:\n‚Ä¢ Phantom (recommended)\n‚Ä¢ Solflare\n‚Ä¢ Backpack\n\nThen refresh this page.';
        alert(message);
        window.open('https://phantom.app/download', '_blank');
        return;
      }

      // Auto-connect if only one wallet available
      if (wallets.length === 1) {
        debug(`üéØ Auto-connecting to ${wallets[0].adapter.name}...`);
        const selectedWallet = wallets[0];
        select(selectedWallet.adapter.name);

        // Give a moment for selection to take effect
        await new Promise(resolve => setTimeout(resolve, 100));
        await connect();
        debug(`‚úÖ Connected to ${selectedWallet.adapter.name}`);
      } else {
        debug(`üì± Multiple wallets found (${wallets.length}), showing menu`);
        setShowWalletMenu(true);
      }
    } catch (error) {
      debug(`‚ùå Connection error: ${error.name}: ${error.message}`);
      console.error('Full wallet error:', error);

      // Don't show alert for user rejections
      if (error.message?.includes('User rejected') || 
          error.message?.includes('rejected the request') ||
          error.name === 'WalletConnectionError') {
        debug('üë§ User cancelled connection');
        return;
      }

      // Show meaningful error for other cases
      alert(`Wallet connection failed:\n${error.message || error.name}\n\nTry refreshing the page or check your wallet.`);
    }
  }, [connected, disconnect, connect, wallets, select, debug]);

  // Handle wallet selection from menu
  const selectWallet = useCallback(async (selectedWallet) => {
    debug(`üéØ Selected ${selectedWallet.adapter.name}`);
    setShowWalletMenu(false);

    try {
      select(selectedWallet.adapter.name);

      // Give selection time to take effect
      await new Promise(resolve => setTimeout(resolve, 200));
      await connect();
      debug(`‚úÖ Successfully connected to ${selectedWallet.adapter.name}`);
    } catch (error) {
      debug(`‚ùå Selection error: ${error.message}`);
      if (!error.message?.includes('User rejected')) {
        alert(`Failed to connect to ${selectedWallet.adapter.name}:\n${error.message}`);
      }
    }
  }, [select, connect, debug]);

  // Enhanced mobile touch handlers
  const handleTouchStart = useCallback((e) => {
    debug('üëÜ Touch start detected');
    setIsPressed(true);

    // Clear any existing timeout
    if (touchTimeoutRef.current) {
      clearTimeout(touchTimeoutRef.current);
    }
  }, [debug]);

  const handleTouchEnd = useCallback((e) => {
    debug('üëÜ Touch end detected');
    e.preventDefault(); // Prevent mouse events
    e.stopPropagation();
    setIsPressed(false);

    // Add haptic feedback if available
    if (navigator.vibrate) {
      navigator.vibrate(50);
    }

    // Delay execution to prevent double-firing
    touchTimeoutRef.current = setTimeout(() => {
      debug('üöÄ Executing touch action');
      handleConnect();
    }, 50);
  }, [handleConnect, debug]);

  // Enhanced click handler for desktop
  const handleClick = useCallback((e) => {
    debug('üñ±Ô∏è Mouse click detected');
    e.preventDefault();
    e.stopPropagation();

    // Don't execute if this came from a touch event
    if (e.detail === 0) { // Touch events have detail = 0
      debug('üö´ Ignoring synthetic click from touch');
      return;
    }

    handleConnect();
  }, [handleConnect, debug]);

  // Close wallet menu when clicking outside
  const handleMenuOverlayClick = useCallback((e) => {
    if (e.target === e.currentTarget) {
      debug('üì± Closing wallet menu');
      setShowWalletMenu(false);
    }
  }, [debug]);

  // Get shortened wallet address in format requested
  const getShortAddress = () => {
    if (!connected || !publicKey) return '';
    const address = publicKey.toString();
    return `${address.substring(0, 4)}...${address.substring(address.length - 4)}`;
  };

  // Get button content based on current state
  const getButtonContent = () => {
    if (connecting) {
      return (
        <div className="flex items-center space-x-2">
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current" />
          <span>Connecting...</span>
        </div>
      );
    }

    if (connected && wallet) {
      return (
        <div className="flex items-center space-x-3">
          <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse" />
          <span>Connected</span>
          {showAddress && (
            <span className="font-mono text-sm opacity-80">
              {getShortAddress()}
            </span>
          )}
          <button
            onClick={(e) => {
              e.stopPropagation();
              handleConnect(); // Will disconnect when connected
            }}
            className="ml-2 px-2 py-1 text-xs bg-red-500/20 hover:bg-red-500/30 text-red-400 rounded border border-red-500/30 transition-colors"
          >
            Logout
          </button>
        </div>
      );
    }

    if (wallets.length === 0) {
      return (
        <div className="flex items-center space-x-2">
          <span className="text-lg">üëõ</span>
          <span>Install Wallet</span>
        </div>
      );
    }

    return (
      <div className="flex items-center space-x-2">
        <span className="text-lg">üëõ</span>
        <span>{children || 'Connect Wallet'}</span>
      </div>
    );
  };

  // Dynamic button styling based on variant
  const getButtonClasses = () => {
    const baseClasses = `
      inline-flex items-center justify-center
      font-bold rounded-xl transition-all duration-200
      cursor-pointer select-none relative
      focus:outline-none focus:ring-4 focus:ring-purple-400/50
      disabled:opacity-50 disabled:cursor-not-allowed
      min-h-[48px] px-6 py-3
      ${isPressed ? 'scale-95 shadow-inner' : 'hover:scale-105 shadow-lg hover:shadow-xl'}
    `;

    const variantClasses = {
      primary: `
        bg-gradient-to-r from-purple-600 to-blue-600 
        hover:from-purple-700 hover:to-blue-700
        active:from-purple-800 active:to-blue-800
        text-white border border-purple-500
        ${connected ? 'from-green-600 to-green-700 hover:from-green-700 hover:to-green-800' : ''}
      `,
      secondary: `
        bg-white border border-gray-300 text-gray-700
        hover:bg-gray-50 hover:border-gray-400
        active:bg-gray-100
        ${connected ? 'bg-green-50 border-green-300 text-green-700' : ''}
      `,
      ghost: `
        bg-transparent border border-purple-400 text-purple-400
        hover:bg-purple-50 hover:text-purple-600
        active:bg-purple-100
        ${connected ? 'border-green-400 text-green-400 hover:text-green-600' : ''}
      `
    };

    return `${baseClasses} ${variantClasses[variant] || variantClasses.primary} ${className}`;
  };

  return (
    <div className="relative">
      {/* Debug Info Overlay */}
      {debugInfo && (
        <div className="absolute -top-12 left-1/2 transform -translate-x-1/2 bg-black text-white text-xs px-2 py-1 rounded whitespace-nowrap z-10">
          {debugInfo}
        </div>
      )}

      {/* Main Button */}
      <button
        className={getButtonClasses()}
        onClick={connected ? undefined : handleClick} // Prevent double clicks when connected
        onTouchStart={handleTouchStart}
        onTouchEnd={handleTouchEnd}
        onContextMenu={(e) => e.preventDefault()}
        disabled={connecting}
        style={{ 
          WebkitTapHighlightColor: 'transparent',
          touchAction: 'manipulation',
          userSelect: 'none'
        }}
        {...props}
      >
        {getButtonContent()}
      </button>

      {/* Wallet Selection Modal */}
      {showWalletMenu && (
        <div 
          className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
          onClick={handleMenuOverlayClick}
        >
          <div className="bg-white rounded-2xl p-6 m-4 max-w-sm w-full shadow-2xl animate-fade-in">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">
                Choose Wallet
              </h3>
              <button
                onClick={() => setShowWalletMenu(false)}
                className="text-gray-400 hover:text-gray-600 text-2xl leading-none"
                style={{ 
                  WebkitTapHighlightColor: 'transparent',
                  touchAction: 'manipulation'
                }}
              >
                √ó
              </button>
            </div>

            <div className="space-y-3 mb-4">
              {wallets.map((walletOption) => (
                <button
                  key={walletOption.adapter.name}
                  onClick={() => selectWallet(walletOption)}
                  className="w-full flex items-center p-4 bg-gray-50 hover:bg-purple-50 rounded-xl transition-all duration-200 border border-gray-200 hover:border-purple-300 hover:scale-102"
                  style={{ 
                    WebkitTapHighlightColor: 'transparent',
                    touchAction: 'manipulation',
                    minHeight: '64px'
                  }}
                >
                  <img
                    src={walletOption.adapter.icon}
                    alt={`${walletOption.adapter.name} icon`}
                    className="w-10 h-10 mr-4 rounded-lg"
                  />
                  <div className="text-left">
                    <div className="text-gray-900 font-semibold text-lg">
                      {walletOption.adapter.name}
                    </div>
                    <div className="text-gray-500 text-sm">
                      {walletOption.readyState === 'Installed' ? 'Detected' : 'Not installed'}
                    </div>
                  </div>
                </button>
              ))}
            </div>

            <div className="text-center text-xs text-gray-500">
              Don't have a wallet? 
              <button
                onClick={() => window.open('https://phantom.app/download', '_blank')}
                className="text-purple-600 hover:text-purple-800 ml-1 underline"
              >
                Get Phantom
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EnhancedWalletButton;

import React, { useState, useRef, useEffect } from 'react';
import { useWallet } from '@solana/wallet-adapter-react';
import EnhancedWalletButton from './EnhancedWalletButton';

const BV7XStyledDashboard = () => {
  const { connected, publicKey, connecting } = useWallet();
  const [activeTab, setActiveTab] = useState('vote');
  const [demoVote, setDemoVote] = useState(null);
  const [showStats, setShowStats] = useState(false);
  const votingRef = useRef(null);

  // Aurora background effect
  useEffect(() => {
    const createAurora = () => {
      const aurora = document.createElement('div');
      aurora.className = 'aurora-bg';
      document.body.appendChild(aurora);
      return () => document.body.removeChild(aurora);
    };

    const cleanup = createAurora();
    return cleanup;
  }, []);

  const userStats = {
    tickets: 156,
    streak: 5,
    winnings: 2.34,
    rank: 42,
    totalVotes: 23,
    winChance: 3.1
  };

  const mockMemes = [
    { 
      id: 1, 
      title: "When SOL hits $1000", 
      image: "/generated/sample-meme-1-crypto-confusion.png",
      votes: 1247,
      trending: true
    },
    { 
      id: 2, 
      title: "DeFi Confusion 101", 
      image: "/generated/sample-meme-2-sol-pump.png",
      votes: 892,
      trending: false
    },
    { 
      id: 3, 
      title: "Diamond Hands Forever", 
      image: "/generated/sample-meme-3-discord-mod.png",
      votes: 654,
      trending: false
    }
  ];

  // Enhanced wallet connection status
  const WalletStatus = () => {
    if (connecting) {
      return (
        <div className="card-glass p-4 flex items-center space-x-3">
          <div className="w-5 h-5 border-2 border-purple-400 border-t-transparent rounded-full animate-spin"></div>
          <span className="text-enhanced-medium text-sm font-medium">Connecting wallet...</span>
        </div>
      );
    }

    if (connected && publicKey) {
      return (
        <div className="card-glass p-4 border-green-500/30 bg-green-500/10">
          <div className="flex items-center justify-center">
            <EnhancedWalletButton 
              variant="ghost" 
              className="w-full" 
              showAddress={true}
            >
              Connected
            </EnhancedWalletButton>
          </div>
        </div>
      );
    }

    return (
      <div className="card-glass p-6 text-center">
        <div className="mb-4">
          <div className="w-16 h-16 mx-auto bg-purple-500/20 rounded-full flex items-center justify-center mb-3">
            <span className="text-2xl">üëõ</span>
          </div>
          <h3 className="text-enhanced-high font-bold mb-2">Connect Your Wallet</h3>
          <p className="text-enhanced-medium text-sm leading-relaxed">
            Connect your Solana wallet to start voting on AI memes and earning SOL rewards
          </p>
        </div>
        <EnhancedWalletButton className="w-full">
          Connect Wallet
        </EnhancedWalletButton>
        <div className="mt-3 text-enhanced-faint text-xs">
          Supports Phantom, Solflare, Backpack & more
        </div>
      </div>
    );
  };

  // Stats cards with BV7X styling
  const StatsGrid = () => {
    const stats = [
      { label: 'Prize Pool', value: '12.7 SOL', subtext: '‚âà $2,540 USD', color: 'text-yellow-400' },
      { label: 'Total Voters', value: '5,247', subtext: 'This week', color: 'text-cyan-400' },
      { label: 'Next Draw', value: 'Sunday', subtext: '8PM UTC', color: 'text-pink-400' },
      { label: 'Winners Paid', value: '47.3 SOL', subtext: 'This month', color: 'text-green-400' }
    ];

    return (
      <div className="responsive-grid-4">
        {stats.map((stat, index) => (
          <div key={index} className="stat-card-enhanced">
            <div className="stat-label-enhanced">{stat.label}</div>
            <div className={`stat-value-enhanced ${stat.color}`}>{stat.value}</div>
            <div className="text-enhanced-faint text-xs">{stat.subtext}</div>
          </div>
        ))}
      </div>
    );
  };

  // User dashboard for connected users
  const UserDashboard = () => {
    if (!connected) return null;

    return (
      <div className="card-glass p-6 border-purple-500/30">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h3 className="text-enhanced-high font-bold text-lg">Your Dashboard</h3>
            <p className="text-enhanced-medium text-sm">Track your voting progress</p>
          </div>
          <button
            onClick={() => setShowStats(!showStats)}
            className="btn-ghost-enhanced text-sm"
          >
            {showStats ? 'Hide' : 'Show'} Stats
          </button>
        </div>

        {showStats && (
          <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            <div className="stat-card-enhanced">
              <div className="stat-label-enhanced">Tickets</div>
              <div className="stat-value-enhanced text-purple-400">{userStats.tickets}</div>
              <div className="text-enhanced-faint text-xs">{userStats.winChance}% win chance</div>
            </div>
            <div className="stat-card-enhanced">
              <div className="stat-label-enhanced">Streak</div>
              <div className="stat-value-enhanced text-orange-400">{userStats.streak} üî•</div>
            </div>
            <div className="stat-card-enhanced">
              <div className="stat-label-enhanced">SOL Earned</div>
              <div className="stat-value-enhanced text-green-400">{userStats.winnings}</div>
            </div>
            <div className="stat-card-enhanced">
              <div className="stat-label-enhanced">Global Rank</div>
              <div className="stat-value-enhanced text-blue-400">#{userStats.rank}</div>
            </div>
          </div>
        )}

        <button
          onClick={() => setActiveTab('vote')}
          className="btn-primary-enhanced w-full"
        >
          üó≥Ô∏è Vote on Today's Memes
        </button>
      </div>
    );
  };

  // Voting interface with BV7X styling
  const VotingInterface = () => {
    const handleVote = (memeId) => {
      if (!connected) {
        alert('Please connect your wallet first!');
        return;
      }
      setDemoVote(memeId);
      setTimeout(() => setDemoVote(null), 3000);
    };

    return (
      <div className="space-y-6">
        <div className="text-center mb-8">
          <h2 className="text-4xl font-bold heading-gradient mb-4">
            Today's AI Memes
          </h2>
          <p className="text-enhanced-medium text-lg">
            Vote for your favorite and earn 8-15 lottery tickets
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {mockMemes.map((meme) => (
            <div 
              key={meme.id} 
              className={`card-glass card-interactive relative overflow-hidden ${
                demoVote === meme.id ? 'border-green-500/50 bg-green-500/10' : ''
              }`}
            >
              {meme.trending && (
                <div className="absolute top-4 left-4 z-10">
                  <div className="status-info-enhanced">
                    üî• Trending
                  </div>
                </div>
              )}

              <div className="p-6">
                <img
                  src={meme.image}
                  alt={meme.title}
                  className="w-full h-48 object-cover rounded-lg mb-4"
                />

                <h3 className="text-enhanced-high font-bold mb-2">{meme.title}</h3>
                <p className="text-enhanced-low text-sm mb-4">
                  {meme.votes.toLocaleString()} votes
