                {demoVote === meme.id ? (
                  <div className="bg-green-500/20 border border-green-500/50 rounded-lg p-4 text-center">
                    <div className="text-green-400 font-bold mb-1">üéâ Vote Recorded!</div>
                    <div className="text-green-300 text-sm">+12 lottery tickets earned</div>
                  </div>
                ) : (
                  <button
                    onClick={() => handleVote(meme.id)}
                    className="btn-primary-enhanced w-full"
                    disabled={!connected}
                  >
                    üó≥Ô∏è Vote ({connected ? 'Earn Tickets' : 'Connect Wallet'})
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>

        {demoVote && (
          <div className="card-glass p-6 bg-green-500/10 border-green-500/30 text-center">
            <h3 className="text-green-400 font-bold text-lg mb-2">Vote Successful! üéâ</h3>
            <p className="text-enhanced-medium">
              You earned <strong className="text-green-400">12 lottery tickets</strong> for Sunday's draw.
              Keep voting daily to build your streak and increase your chances!
            </p>
          </div>
        )}
      </div>
    );
  };

  // Navigation tabs
  const TabNavigation = () => {
    const tabs = [
      { id: 'vote', label: 'üó≥Ô∏è Vote', badge: '3 New' },
      { id: 'stats', label: 'üìä Stats' },
      { id: 'winners', label: 'üèÜ Winners' }
    ];

    return (
      <div className="flex space-x-4 mb-8">
        {tabs.map((tab) => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            className={`btn-ghost-enhanced relative ${
              activeTab === tab.id ? 'border-purple-500/50 bg-purple-500/10' : ''
            }`}
          >
            {tab.label}
            {tab.badge && (
              <div className="absolute -top-2 -right-2 bg-red-500 text-white text-xs px-2 py-0.5 rounded-full">
                {tab.badge}
              </div>
            )}
          </button>
        ))}
      </div>
    );
  };

  return (
    <div className="min-h-screen relative">
      {/* Main Content */}
      <div className="relative z-10">
        {/* Header */}
        <header className="nav-enhanced">
          <div className="max-w-7xl mx-auto section-padding py-4">
            <div className="flex items-center justify-between">
              {/* Logo */}
              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 bg-gradient-to-r from-purple-600 to-cyan-600 rounded-xl flex items-center justify-center">
                  <span className="text-white font-bold text-xl">üó≥Ô∏è</span>
                </div>
                <div>
                  <div className="text-enhanced-high font-bold text-xl">MemeForge</div>
                  <div className="text-enhanced-low text-xs mono">AI Dreams. Democracy Decides.</div>
                </div>
